.wui-step {
  font-size: 13px;
}
.wui-step-content {
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
}
.wui-step-item {
  -webkit-box-flex: 1;
      -ms-flex: 1;
          flex: 1;
  position: relative;
}
.wui-step-item:not(:first-child):before {
  content: '';
  height: 2px;
  position: absolute;
  top: -1px;
  background-color: #CCC;
}
.wui-step-item > em {
  border-radius: 50%;
  position: absolute;
  top: 50%;
  left: 50%;
  background-color: #CCC;
}
.wui-step-item-top,
.wui-step-item-bottom {
  position: absolute;
  left: 0;
  text-align: center;
  white-space: nowrap;
  text-overflow: ellipsis;
  width: 100%;
  padding: 0 4px;
}
.wui-step-item-top-text > span {
  color: #989898;
}
.wui-step-item-bottom {
  color: #333;
}
.wui-step-theme-bottom .wui-step-content {
  padding: 10px 0 42px 0;
}
.wui-step-theme-bottom .wui-step-item:not(:first-child):before {
  width: 70%;
  left: -35%;
}
.wui-step-theme-bottom .wui-step-item > em {
  width: 20px;
  height: 20px;
  margin-left: -10px;
  margin-top: -10px;
  text-align: center;
  line-height: 20px;
  font-size: 12px;
}
.wui-step-theme-bottom .wui-step-item > em > i {
  color: #FFF;
}
.wui-step-theme-bottom .wui-step-item-top {
  bottom: 18px;
}
.wui-step-theme-bottom .wui-step-item-bottom {
  top: 18px;
}
.wui-step-theme-bottom .wui-step-item-current:before {
